vice`.`sm_board_post_list` (`title`, `content`, `status`, `poster`, `createTime`, `updateTime`, `view`, `comment`, `authorId`, `commentId`, `categoryId`, `channelId`, `codeCount`, `postType`) VALUES ("游客订淄博酒店被通知只用付一半，网友惊叹：这是“坐地降价”", "<p>https://www.guancha.cn/video/gczvideo/content.html?id=18165</p><p>我都想去淄博了。</p><p>这一波操作真是六。</p><p></p>", "OFFLINE", "", 1682494041000, 1682494041000, 0, 0, "5ba1a83e33cf47d182b98ac1fe1beac8", NULL, "c8d614e56acb8a192ec4af8b375a5eea", "897f7890249777c2ecdf89e25ce9ff4c", 0, 1);INSERT INTO `nodejs-service`.`sm_board_post_list` (`title`, `content`, `status`, `poster`, `createTime`, `updateTime`, `view`, `comment`, `authorId`, `commentId`, `categoryId`, `channelId`, `codeCount`, `postType`) VALUES ("从身边情况研究女人出轨的情况", "<p>80%都是跟上级或者同事，上级为主</p><p>因为工作中上级如果看上哪个女人</p><p>只需要在工作中稍微照顾一下</p><p>说几句好听的</p><p>然后随便就拿下了</p><p>女人几乎没有还手之力</p><p>如果拒绝的话，就很可能要被穿小鞋</p><p>所以这就能解释有的体制内领导能有几百个情妇</p><p>有几百个就有几十个的</p><p>有几个的更是数不胜数</p><p></p>", "OFFLINE", "", 1682494361000, 1682494361000, 0, 0, "5ba1a83e33cf47d182b98ac1fe1beac8", NULL, "c8d614e56acb8a192ec4af8b375a5eea", "897f7890249777c2ecdf89e25ce9ff4c", 0, 1);INSERT INTO `nodejs-service`.`sm_board_post_list` (`title`, `content`, `status`, `poster`, `createTime`, `updateTime`, `view`, `comment`, `authorId`, `commentId`, `categoryId`, `channelId`, `codeCount`, `postType`) VALUES ("致中小学生及家长的一封信", "<p>尊敬的家长朋友们、亲爱的同学们：</p><p>近年来，违规电动三四轮车非法上路、乱行乱停问题突出。这些违规车辆未经许可生产，性能不符合安全标准，极易引发交通、火灾等安全事故，严重危害人民群众生命财产安全。</p><p>我市已于&nbsp;2021&nbsp;年&nbsp;7&nbsp;月&nbsp;12&nbsp;日发布通告，明确违规电动三四轮车过渡期截止&nbsp;2023&nbsp;年&nbsp;12&nbsp;月&nbsp;31&nbsp;日，过渡期后不得上路行驶，不得在公共场所停放。违规上路行驶或停放的，交管部门将依法予以查处。</p><p>为了您和家人的安全，我们倡议：</p><p>一、&nbsp;自觉安全守法出行。请自觉遵守交通法律法规，文明守法步行、骑车、行车、停车，做到不逆行、不闯灯越线、不乱停乱放，自觉礼让斑马线。遇民警执勤执法时，请服从指挥，配合执法。</p><p>二、依法依规使用车辆。有违规电动三四轮车的，请尽快处置。对属于工信部《道路机动车辆生产企业及产品公告》范围内的电动三轮车，应依据现有规定尽快到交管部门办理机动车注册登记（户籍地址或暂住地址为门头沟、通州、平谷、密云、怀&nbsp;柔、顺义、昌平、延庆、房山、大兴区的单位及个人方可申请注册登记京&nbsp;B&nbsp;号牌摩托车）。驾驶上述机动车应持相应准驾车型驾驶证，并遵守本市六环路（含）内道路禁止正三轮摩托车通行等相关政策规定。对驾驶无牌机动车上路行驶的，交管部门将依法扣留车辆，并处罚款处罚。对未取得驾驶证或驾驶证与准驾车型不符驾驶机动车上路行驶的，将依法予以罚款、拘留处罚。</p><p>三、传播文明交通理念。请大家积极践行并传播文明交通理念，主动劝阻不文明交通行为，如果您的家人、亲戚、朋友有违规电动三四轮车的，请及时提醒他们尽快处置。</p><p>衷心感谢您的理解支持！</p><p></p>", "OFFLINE", "", 1682495292000, 1682495292000, 0, 0, "5ba1a83e33cf47d182b98ac1fe1beac8", NULL, "c8d614e56acb8a192ec4af8b375a5eea", "897f7890249777c2ecdf89e25ce9ff4c", 0, 1);INSERT INTO `nodejs-service`.`sm_board_post_list` (`title`, `content`, `status`, `poster`, `createTime`, `updateTime`, `view`, `comment`, `authorId`, `commentId`, `categoryId`, `channelId`, `codeCount`, `postType`) VALUES ("固体硬盘跌价刹不住车了", "<p>东芝原厂带缓存的1T硬盘，跌到300以内了</p><p></p>", "OFFLINE", "", 1682495292000, 1682495292000, 0, 0, "5ba1a83e33cf47d182b98ac1fe1beac8", NULL, "c8d614e56acb8a192ec4af8b375a5eea", "897f7890249777c2ecdf89e25ce9ff4c", 0, 1);INSERT INTO `nodejs-service`.`sm_board_post_list` (`title`, `content`, `status`, `poster`, `createTime`, `updateTime`, `view`, `comment`, `authorId`, `commentId`, `categoryId`, `channelId`, `codeCount`, `postType`) VALUES ("我发现：双标是人的本性之一！", "<p>比如说，大多数看，看见别人占便宜都要抨击，轮到自己了，巴不得呢</p><p></p>", "OFFLINE", "", 1682494063000, 1682494063000, 0, 0, "5ba1a83e33cf47d182b98ac1fe1beac8", NULL, "c8d614e56acb8a192ec4af8b375a5eea", "897f7890249777c2ecdf89e25ce9ff4c", 0, 1);INSERT INTO `nodejs-service`.`sm_board_post_list` (`title`, `content`, `status`, `poster`, `createTime`, `updateTime`, `view`, `comment`, `authorId`, `commentId`, `categoryId`, `channelId`, `codeCount`, `postType`) VALUES ("现在大学校园是不是都不能随便进了？？", "<p>从疫情开始的？&nbsp;基本上是个大学进出都要刷卡吧？</p><p></p>", "OFFLINE", "", 1682489045000, 1682489045000, 0, 0, "5ba1a83e33cf47d182b98ac1fe1beac8", NULL, "c8d614e56acb8a192ec4af8b375a5eea", "897f7890249777c2ecdf89e25ce9ff4c", 0, 1);INSERT INTO `nodejs-service`.`sm_board_post_list` (`title`, `content`, `status`, `poster`, `createTime`, `updateTime`, `view`, `comment`, `authorId`, `commentId`, `categoryId`, `channelId`, `codeCount`, `postType`) VALUES ("鲁大师最新表态", "<p></p><p><img src='http://192.168.200.130:9000/blog-service-oss/20230426/7f7b45e25ad144868d7a2f23312c07a3.png' alt='' data-src='http://192.168.200.130:9000/blog-service-oss/20230426/7f7b45e25ad144868d7a2f23312c07a3.png' /></p>", "OFFLINE", "", 1682489098000, 1682489098000, 0, 0, "5ba1a83e33cf47d182b98ac1fe1beac8", NULL, "c8d614e56acb8a192ec4af8b375a5eea", "897f7890249777c2ecdf89e25ce9ff4c", 0, 1);INSERT INTO `nodejs-service`.`sm_board_post_list` (`title`, `content`, `status`, `poster`, `createTime`, `updateTime`, `view`, `comment`, `authorId`, `commentId`, `categoryId`, `channelId`, `codeCount`, `postType`) VALUES ("上海人所谓的“洋气”其实特别没文化", "<p>比如做核酸排队喝红酒。</p><p>比如公交车站煮咖啡听黑胶。</p><p>比如把日料捧上天。。。</p><p>感觉太Low了</p><p></p>", "OFFLINE", "", 1682493704000, 1682493704000, 0, 0, "5ba1a83e33cf47d182b98ac1fe1beac8", NULL, "c8d614e56acb8a192ec4af8b375a5eea", "897f7890249777c2ecdf89e25ce9ff4c", 0, 1);INSERT INTO `nodejs-service`.`sm_board_post_list` (`title`, `content`, `status`, `poster`, `createTime`, `updateTime`, `view`, `comment`, `authorId`, `commentId`, `categoryId`, `channelId`, `codeCount`, `postType`) VALUES ("美帝怎么种这么多粮食", "<p>根据2007年的数据统计，美国的玉米产量占到了世界总产量的42.32%，大豆产量占世界总产量的32.71%。</p><p>能够种出世界超过三分之一玉米和大豆的美国，人口有多少呢？不到世界的5%。</p><p></p>", "OFFLINE", "", 1682491947000, 1682491947000, 0, 0, "5ba1a83e33cf47d182b98ac1fe1beac8", NULL, "c8d614e56acb8a192ec4af8b375a5eea", "897f7890249777c2ecdf89e25ce9ff4c", 0, 1);INSERT INTO `nodejs-service`.`sm_board_post_list` (`title`, `content`, `status`, `poster`, `createTime`, `updateTime`, `view`, `comment`, `authorId`, `commentId`, `categoryId`, `channelId`, `codeCount`, `postType`) VALUES ("沙俄对东三省垂涎三尺吧", "<p>可惜就是搞不到手，哈哈</p><p></p>", "OFFLINE", "", 1682490123000, 1682490123000, 0, 0, "5ba1a83e33cf47d182b98ac1fe1beac8", NULL, "c8d614e56acb8a192ec4af8b375a5eea", "897f7890249777c2ecdf89e25ce9ff4c", 0, 1);INSERT INTO `nodejs-service`.`sm_board_post_list` (`title`, `content`, `status`, `poster`, `createTime`, `updateTime`, `view`, `comment`, `authorId`, `commentId`, `categoryId`, `channelId`, `codeCount`, `postType`) VALUES ("跟日韩关系肉眼可见的下降了", "<p>疫情前还在讨论中日韩自贸区。美国功不可没</p><p>-&nbsp;来自&nbsp;水木社区APP&nbsp;v3.5.7</p><p></p>", "OFFLINE", "", 1682492538236, 1682492538236, 0, 0, "5ba1a83e33cf47d182b98ac1fe1beac8", NULL, "c8d614e56acb8a192ec4af8b375a5eea", "897f7890249777c2ecdf89e25ce9ff4c", 0, 1);INSERT INTO `nodejs-service`.`sm_board_post_list` (`title`, `content`, `status`, `poster`, `createTime`, `updateTime`, `view`, `comment`, `authorId`, `commentId`, `categoryId`, `channelId`, `codeCount`, `postType`) VALUES ("北上租房市场确实不行", "<p>这两个地方人口都流出不少，去二线了，长沙流入最多。年轻人看开了</p><p></p>", "OFFLINE", "", 1682495080705, 1682495080705, 0, 0, "5ba1a83e33cf47d182b98ac1fe1beac8", NULL, "c8d614e56acb8a192ec4af8b375a5eea", "897f7890249777c2ecdf89e25ce9ff4c", 0, 1);INSERT INTO `nodejs-service`.`sm_board_post_list` (`title`, `content`, `status`, `poster`, `createTime`, `updateTime`, `view`, `comment`, `authorId`, `commentId`, `categoryId`, `channelId`, `codeCount`, `postType`) VALUES ("绿豆汤怎么熬才不会发红", "<p>att</p><p></p>", "OFFLINE", "", 1682488486000, 1682488486000, 0, 0, "5ba1a83e33cf47d182b98ac1fe1beac8", NULL, "c8d614e56acb8a192ec4af8b375a5eea", "897f7890249777c2ecdf89e25ce9ff4c", 0, 1);INSERT INTO `nodejs-service`.`sm_board_post_list` (`title`, `content`, `status`, `poster`, `createTime`, `updateTime`, `view`, `comment`, `authorId`, `commentId`, `categoryId`, `channelId`, `codeCount`, `postType`) VALUES ("我深圳的朋友有句话，经济好不好，就看月亮湾大道吵不吵", "<p>我这朋友在深圳，房子买在月亮湾大道的边上。这条路是通往蛇口码头的交通要道，很多货柜车都走这条路。</p><p>他刚入住的时候是07年那会，当时觉得房子特别吵，装隔音玻璃也没用，一度跑到客厅睡沙发。</p><p>后来08年经济下行，他发现路上的大货车少多了，晚上在房间也能睡得着了。</p><p>昨天晚上跟他聊天，他说仿佛又有了08年的感觉，路上大货的噪音比往年少了很多。</p><p></p>", "OFFLINE", "", 1682488971000, 1682488971000, 0, 0, "5ba1a83e33cf47d182b98ac1fe1beac8", NULL, "c8d614e56acb8a192ec4af8b375a5eea", "897f7890249777c2ecdf89e25ce9ff4c", 0, 1);INSERT INTO `nodejs-service`.`sm_board_post_list` (`title`, `content`, `status`, `poster`, `createTime`, `updateTime`, `view`, `comment`, `authorId`, `commentId`, `categoryId`, `channelId`, `codeCount`, `postType`) VALUES ("这种二手的笔记本能买吗？", "<p>便宜1000块钱呢</p><p>https://item.jd.com/10057427942003.html#none</p><p></p>", "OFFLINE", "", 1682495019000, 1682495019000, 0, 0, "5ba1a83e33cf47d182b98ac1fe1beac8", NULL, "c8d614e56acb8a192ec4af8b375a5eea", "897f7890249777c2ecdf89e25ce9ff4c", 0, 1);INSERT INTO `nodejs-service`.`sm_board_post_list` (`title`, `content`, `status`, `poster`, `createTime`, `updateTime`, `view`, `comment`, `authorId`, `commentId`, `categoryId`, `channelId`, `codeCount`, `postType`) VALUES ("驾龄10万公里，北京基本可以不用导航了", "<p>一般出门前看一眼目的地在哪就行，没去过的地方，接近1公里的时候开导航知道怎么进入</p><p>我说的是北京全境</p><p></p>", "OFFLINE", "", 1682411630000, 1682411630000, 0, 0, "5ba1a83e33cf47d182b98ac1fe1beac8", NULL, "1c455a5dccf4242008d188f9676e3f4e", "e8d1470f8c33b86d8dae444090e81be4", 0, 1);INSERT INTO `nodejs-service`.`sm_board_post_list` (`title`, `content`, `status`, `poster`, `createTime`, `updateTime`, `view`, `comment`, `authorId`, `commentId`, `categoryId`, `channelId`, `codeCount`, `postType`) VALUES ("出售威马ex5 400探索版，宁德时代电池", "<p>有没有还在电号排队，想提前买车的，出售威马ex5探索版10.88万，可三年后过户，期间你随便开，延长期限也可谈，有需要的微信联系wwwplanet</p><p></p>", "OFFLINE", "", 1682131707000, 1682131707000, 0, 0, "5ba1a83e33cf47d182b98ac1fe1beac8", NULL, "3092a8362193dc5b12850b5e30055b01", "e8d1470f8c33b86d8dae444090e81be4", 0, 1);INSERT INTO `nodejs-service`.`sm_board_post_list` (`title`, `content`, `status`, `poster`, `createTime`, `updateTime`, `view`, `comment`, `authorId`, `commentId`, `categoryId`, `channelId`, `codeCount`, `postType`) VALUES ("别克也快把自己玩死了", "<p>周末看了看别克，本来想看看E5，结果没有</p><p>就随便看了看其他的车，做工，外形设计，越来越差</p><p>以前的别克，设计好看，做工细腻，有豪华感。现在看看钣金，还不如，国产车</p><p>越来越不行了</p><p></p>", "OFFLINE", "", 1682408429000, 1682408429000, 0, 0, "5ba1a83e33cf47d182b98ac1fe1beac8", NULL, "1c455a5dccf4242008d188f9676e3f4e", "e8d1470f8c33b86d8dae444090e81be4", 0, 1);INSERT INTO `nodejs-service`.`sm_board_post_list` (`title`, `content`, `status`, `poster`, `createTime`, `updateTime`, `view`, `comment`, `authorId`, `commentId`, `categoryId`, `channelId`, `codeCount`, `postType`) VALUES ("以后油价会不会12元一升", "<p>在国际油价80到100美元之间波动的时候。</p><p>有关部门强行将油价哄抬上去，看你们还不买电车。</p><p></p>", "OFFLINE", "", 1682434643000, 1682434643000, 0, 0, "5ba1a83e33cf47d182b98ac1fe1beac8", NULL, "1c455a5dccf4242008d188f9676e3f4e", "e8d1470f8c33b86d8dae444090e81be4", 0, 1);INSERT INTO `nodejs-service`.`sm_board_post_list` (`title`, `content`, `status`, `poster`, `createTime`, `updateTime`, `view`, `comment`, `authorId`, `commentId`, `categoryId`, `channelId`, `codeCount`, `postType`) VALUES ("帝都蓝牌买比亚迪混动是要换绿牌吗？", "<p>需求直接被拦下，看来直接买油车了</p><p>-&nbsp;来自&nbsp;水木社区APP&nbsp;v3.5.6</p><p></p>", "OFFLINE", "", 1682396357000, 1682396357000, 0, 0, "5ba1a83e33cf47d182b98ac1fe1beac8", NULL, "1c455a5dccf4242008d188f9676e3f4e", "e8d1470f8c33b86d8dae444090e81be4", 0, 1);INSERT INTO `nodejs-service`.`sm_board_post_list` (`title`, `content`, `status`, `poster`, `createTime`, `updateTime`, `view`, `comment`, `authorId`, `commentId`, `categoryId`, `channelId`, `codeCount`, `postType`) VALUES ("两年前还有朋友瞧不起比亚迪", "<p>说自主品牌都不靠谱，他换车只看大众。</p><p>可是，他不知道的是，当时大众车的平均售价已经低于比亚迪。</p><p>发自「今日水木&nbsp;on&nbsp;ELS-AN00」</p><p></p>", "OFFLINE", "", 1682388122000, 1682388122000, 0, 0, "5ba1a83e33cf47d182b98ac1fe1beac8", NULL, "1c455a5dccf4242008d188f9676e3f4e", "e8d1470f8c33b86d8dae444090e81be4", 0, 1);INSERT INTO `nodejs-service`.`sm_board_post_list` (`title`, `content`, `status`, `poster`, `createTime`, `updateTime`, `view`, `comment`, `authorId`, `commentId`, `categoryId`, `channelId`, `codeCount`, `postType`) VALUES ("新能源的尽头是氢能", "<p>可能以后内燃机直接简单粗暴烧氢气，所以大家珍惜现在背个大电池满街跑的机会吧。</p><p></p>", "OFFLINE", "", 1680527072000, 1680527072000, 0, 0, "5ba1a83e33cf47d182b98ac1fe1beac8", NULL, "447bffd6152246c2092283a0a4d49bd8", "e8d1470f8c33b86d8dae444090e81be4", 0, 1);INSERT INTO `nodejs-service`.`sm_board_post_list` (`title`, `content`, `status`, `poster`, `createTime`, `updateTime`, `view`, `comment`, `authorId`, `commentId`, `categoryId`, `channelId`, `codeCount`, `postType`) VALUES ("研究了半天新ES6，决定还是定新ES8", "<p>逛了下上海车展，还是决定下定新8</p><p>新6确实漂亮，清秀又俊朗</p><p>但是新8真的是霸气十足</p><p></p>", "OFFLINE", "", 1682487854000, 1682487854000, 0, 0, "5ba1a83e33cf47d182b98ac1fe1beac8", NULL, "447bffd6152246c2092283a0a4d49bd8", "e8d1470f8c33b86d8dae444090e81be4", 0, 1);INSERT INTO `nodejs-service`.`sm_board_post_list` (`title`, `content`, `status`, `poster`, `createTime`, `updateTime`, `view`, `comment`, `authorId`, `commentId`, `categoryId`, `channelId`, `codeCount`, `postType`) VALUES ("提车遇到点问题，求建议", "<p>同事刚买完比亚迪宋，交完钱态度马上就敷衍了，下午2点去，8点手续都没走完，车没提出来。</p><p>第二天再去，提出来了，一堆问题，现在正和售后折腾中。</p><p>同事年轻人，功课没做好，提的车是去年11月份生产的，轮胎去年5月份的，钥匙上一堆划痕，里程显示12公里。</p><p>和有车的同事聊，都说觉得不对劲。不知道版上有没有建议，同事现在要求验车。</p><p></p>", "OFFLINE", "", 1682406517000, 1682406517000, 0, 0, "5ba1a83e33cf47d182b98ac1fe1beac8", NULL, "447bffd6152246c2092283a0a4d49bd8", "e8d1470f8c33b86d8dae444090e81be4", 0, 1);INSERT INTO `nodejs-service`.`sm_board_post_list` (`title`, `content`, `status`, `poster`, `createTime`, `updateTime`, `view`, `comment`, `authorId`, `commentId`, `categoryId`, `channelId`, `codeCount`, `postType`) VALUES ("AI应用叫好不叫座", "<p>AIGC和ChatGPT发展趋势看好，但是无法产生效益。对用户收费，用户不一定买单，只能对商家收费，再转嫁给用户消费者</p><p>自动驾驶也一样，L2+的辅助驾驶不是必需品，消费者还是自己可以开车，不一定用得上，即使用得上也是低频使用场景。L4/L5就更不用说了，也不是必需品。除非自动驾驶发展后，上路后车主不能碰车的方向盘或者车没有方向盘，但是15年内很难看得到。</p><p>AI应用于机器人之类，在工厂有了一些应用场景，但是无法渗透到家庭和个人生活</p><p>还没有象移动支付一样改变人类生活的应用场景</p><p>-&nbsp;来自&nbsp;水木社区APP&nbsp;v3.5.7</p><p></p>", "OFFLINE", "", 1682469217761, 1682469217761, 0, 0, "5ba1a83e33cf47d182b98ac1fe1beac8", NULL, "447bffd6152246c2092283a0a4d49bd8", "e8d1470f8c33b86d8dae444090e81be4", 0, 1);INSERT INTO `nodejs-service`.`sm_board_post_list` (`title`, `content`, `status`, `poster`, `createTime`, `updateTime`, `view`, `comment`, `authorId`, `commentId`, `categoryId`, `channelId`, `codeCount`, `postType`) VALUES ("车版一个严重心理疾病患者", "<p>https://m.newsmth.net/article/AutoWorld/1944556932</p><p>真不知现实中怎样的存在……</p><p>发自「今日水木&nbsp;on&nbsp;LIO-AN00」</p><p></p><p><img src='http://192.168.200.130:9000/blog-service-oss/20230426/8e690dd08b894368a4a779da83ed59ac.jpg' alt='' data-src='http://192.168.200.130:9000/blog-service-oss/20230426/8e690dd08b894368a4a779da83ed59ac.jpg' /></p><p><img src='http://192.168.200.130:9000/blog-service-oss/20230426/8f1f6ec9c0384c4fb66faf25cb1dded0.jpg' alt='' data-src='http://192.168.200.130:9000/blog-service-oss/20230426/8f1f6ec9c0384c4fb66faf25cb1dded0.jpg' /></p><p><img src='http://192.168.200.130:9000/blog-service-oss/20230426/534860670de24b7c84a0e8023cda9164.jpg' alt='' data-src='http://192.168.200.130:9000/blog-service-oss/20230426/534860670de24b7c84a0e8023cda9164.jpg' /></p>", "OFFLINE", "", 1682428655000, 1682428655000, 0, 0, "5ba1a83e33cf47d182b98ac1fe1beac8", NULL, "1c455a5dccf4242008d188f9676e3f4e", "e8d1470f8c33b86d8dae444090e81be4", 0, 1);INSERT INTO `nodejs-service`.`sm_board_post_list` (`title`, `content`, `status`, `poster`, `createTime`, `updateTime`, `view`, `comment`, `authorId`, `commentId`, `categoryId`, `channelId`, `codeCount`, `postType`) VALUES ("现在还有免费办etc 的地方吗", "<p>乐速通app&nbsp;吗</p><p></p>", "OFFLINE", "", 1682491144000, 1682491144000, 0, 0, "5ba1a83e33cf47d182b98ac1fe1beac8", NULL, "447bffd6152246c2092283a0a4d49bd8", "e8d1470f8c33b86d8dae444090e81be4", 0, 1);INSERT INTO `nodejs-service`.`sm_board_post_list` (`title`, `content`, `status`, `poster`, `createTime`, `updateTime`, `view`, `comment`, `authorId`, `commentId`, `categoryId`, `channelId`, `codeCount`, `postType`) VALUES ("特斯拉美国制造成本好高", "<p>加上加班费和股权，一个小时工资27.5刀，折合人民币190元一小时，如果一个工人平均一周50小时的话(算上加班)，一年52个礼拜，那年薪要50万人民币了。这造车成本也太高了。</p><p>看网上说，上海工厂的工人，算上17薪，平均一个月才一万，一年12万，上海工人工资成本才美国的1/4，估计素质还更高。</p><p>-&nbsp;来自&nbsp;水木社区APP&nbsp;v3.5.7</p><p></p>", "OFFLINE", "", 1682386520099, 1682386520099, 0, 0, "5ba1a83e33cf47d182b98ac1fe1beac8", NULL, "447bffd6152246c2092283a0a4d49bd8", "e8d1470f8c33b86d8dae444090e81be4", 0, 1);INSERT INTO `nodejs-service`.`sm_board_post_list` (`title`, `content`, `status`, `poster`, `createTime`, `updateTime`, `view`, `comment`, `authorId`, `commentId`, `categoryId`, `channelId`, `codeCount`, `postType`) VALUES ("京津之间跑通勤续航能力多少合适？", "<p>不到500的是不是不太够呢？往返300公里。</p><p></p>", "OFFLINE", "", 1682473570000, 1682473570000, 0, 0, "5ba1a83e33cf47d182b98ac1fe1beac8", NULL, "447bffd6152246c2092283a0a4d49bd8", "e8d1470f8c33b86d8dae444090e81be4", 0, 1);INSERT INTO `nodejs-service`.`sm_board_post_list` (`title`, `content`, `status`, `poster`, `createTime`, `updateTime`, `view`, `comment`, `authorId`, `commentId`, `categoryId`, `channelId`, `codeCount`, `postType`) VALUES ("电池衰减后，充电效率有变化吗", "<p>比如我一开始充了100kw，</p><p>然后能用95kw。</p><p>后来电池衰减了20kw。</p><p>1.我只能充80kw,&nbsp;然后用75kw。</p><p>2.我能充100kw,&nbsp;但只能用75kw。</p><p></p>", "OFFLINE", "", 1682484816000, 1682484816000, 0, 0, "5ba1a83e33cf47d182b98ac1fe1beac8", NULL, "447bffd6152246c2092283a0a4d49bd8", "e8d1470f8c33b86d8dae444090e81be4", 0, 1);INSERT INTO `nodejs-service`.`sm_board_post_list` (`title`, `content`, `status`, `poster`, `createTime`, `updateTime`, `view`, `comment`, `authorId`, `commentId`, `categoryId`, `channelId`, `codeCount`, `postType`) VALUES ("现代第一季度：盈利172亿人民币", "<p>现代汽车第一季度净利润3.31万亿（约合人民币172.12亿元）好于预期</p><p>据报道，现代汽车第一季度营业利润3.59万亿韩元（约合人民币186.68亿元），同比增长86%，预估2.84万亿韩元（约合人民币147.68亿元）。</p><p>净利润3.31万亿韩元（约合人民币172.12亿元）上年同期1.58万亿韩元（约合人民币82.16亿元），预估2.26万亿韩元（约合人民币117.52亿元）。</p><p>销售额37.78万亿韩元（约合人民币1964.56亿元），同比增长25%，预估36.47万亿韩元（约合人民币1896.44亿元）。</p><p>全球销售量：1,021,712辆汽车，同比增长13.2%。</p><p>韩国以外的汽车销量：830,665辆，同比增长10.7%。</p><p>韩国本地销量：191,047辆，同比增长25.6%。</p><p></p>", "OFFLINE", "", 1682429762000, 1682429762000, 0, 0, "5ba1a83e33cf47d182b98ac1fe1beac8", NULL, "1c455a5dccf4242008d188f9676e3f4e", "e8d1470f8c33b86d8dae444090e81be4", 0, 1);INSERT INTO `nodejs-service`.`sm_board_post_list` (`title`, `content`, `status`, `poster`, `createTime`, `updateTime`, `view`, `comment`, `authorId`, `commentId`, `categoryId`, `channelId`, `codeCount`, `postType`) VALUES ("理想公布新一周数据了。。", "<p>如图</p><p>-&nbsp;来自&nbsp;水木社区APP&nbsp;v3.5.7</p><p></p>", "OFFLINE", "", 1682403506000, 1682403506000, 0, 0, "5ba1a83e33cf47d182b98ac1fe1beac8", NULL, "447bffd6152246c2092283a0a4d49bd8", "e8d1470f8c33b86d8dae444090e81be4", 0, 1);INSERT INTO `nodejs-service`.`sm_board_post_list` (`title`, `content`, `status`, `poster`, `createTime`, `updateTime`, `view`, `comment`, `authorId`, `commentId`, `categoryId`, `channelId`, `codeCount`, `postType`) VALUES ("现在京津检查站都不查车了", "<p>身份证也不查，直接杆子抬落一下就过了</p><p></p>", "OFFLINE", "", 1682421507000, 1682421507000, 0, 0, "5ba1a83e33cf47d182b98ac1fe1beac8", NULL, "1c455a5dccf4242008d188f9676e3f4e", "e8d1470f8c33b86d8dae444090e81be4", 0, 1);INSERT INTO `nodejs-service`.`sm_board_post_list` (`title`, `content`, `status`, `poster`, `createTime`, `updateTime`, `view`, `comment`, `authorId`, `commentId`, `categoryId`, `channelId`, `codeCount`, `postType`) VALUES ("[建议]北京电动车外迁置换的问题", "<p>本人现有京牌电动车一辆，近期想置换一辆新电车，打算外迁享受北京市补贴（8000）+置换补贴。</p><p>媳妇去年新能源指标未用，是否可以把我现在的车外迁到河北给媳妇，然后媳妇用北京新能源指标把这个车从河北迁入到北京？</p><p></p>", "OFFLINE", "", 1682478984000, 1682478984000, 0, 0, "5ba1a83e33cf47d182b98ac1fe1beac8", NULL, "447bffd6152246c2092283a0a4d49bd8", "e8d1470f8c33b86d8dae444090e81be4", 0, 1);INSERT INTO `nodejs-service`.`sm_board_post_list` (`title`, `content`, `status`, `poster`, `createTime`, `updateTime`, `view`, `comment`, `authorId`, `commentId`, `categoryId`, `channelId`, `codeCount`, `postType`) VALUES ("只想去石家庄走哪边呢？", "<p>路牌不清晰吧</p><p></p>", "OFFLINE", "", 1682464066745, 1682464066745, 0, 0, "5ba1a83e33cf47d182b98ac1fe1beac8", NULL, "1c455a5dccf4242008d188f9676e3f4e", "e8d1470f8c33b86d8dae444090e81be4", 0, 1);INSERT INTO `nodejs-service`.`sm_board_post_list` (`title`, `content`, `status`, `poster`, `createTime`, `updateTime`, `view`, `comment`, `authorId`, `commentId`, `categoryId`, `channelId`, `codeCount`, `postType`) VALUES ("碰路牙石上了，这种需要换轮胎吗？五一开高速", "<p>21年8月新车原胎（唐dmi，原胎质量一般），怼到路牙石了，伤在靠近轮毂那里，如图。</p><p>请问一下这种需要换新轮胎吗？</p><p>五一开高速，单程大概700公里，不换胎安全吗？</p><p>另外车上没有备胎。</p><p></p><p><img src='http://192.168.200.130:9000/blog-service-oss/20230426/16826266fad040b5b0191978255a6a11.jpg' alt='' data-src='http://192.168.200.130:9000/blog-service-oss/20230426/16826266fad040b5b0191978255a6a11.jpg' /></p><p><img src='http://192.168.200.130:9000/blog-service-oss/20230426/141acef1e76d4774aba337ecaf21469c.jpg' alt='' data-src='http://192.168.200.130:9000/blog-service-oss/20230426/141acef1e76d4774aba337ecaf21469c.jpg' /></p>", "OFFLINE", "", 1682270870000, 1682270870000, 0, 0, "5ba1a83e33cf47d182b98ac1fe1beac8", NULL, "1c455a5dccf4242008d188f9676e3f4e", "e8d1470f8c33b86d8dae444090e81be4", 0, 1);INSERT INTO `nodejs-service`.`sm_board_post_list` (`title`, `content`, `status`, `poster`, `createTime`, `updateTime`, `view`, `comment`, `authorId`, `commentId`, `categoryId`, `channelId`, `codeCount`, `postType`) VALUES ("亲们，七座电车，q5etron咋样", "<p>跟前面同学需求类似，需要7座。</p><p>唐ev地盘太低，离地间隙16cm，担心拖底，电池包就完蛋了。</p><p>id6没啥感觉，不喜欢。</p><p>最近看了q5e，价格跟唐ev差不多，配置当然低不少。</p><p>q5e可入吗？</p><p></p>", "OFFLINE", "", 1680080175000, 1680080175000, 0, 0, "5ba1a83e33cf47d182b98ac1fe1beac8", NULL, "447bffd6152246c2092283a0a4d49bd8", "e8d1470f8c33b86d8dae444090e81be4", 0, 1);INSERT INTO `nodejs-service`.`sm_board_post_list` (`title`, `content`, `status`, `poster`, `createTime`, `updateTime`, `view`, `comment`, `authorId`, `commentId`, `categoryId`, `channelId`, `codeCount`, `postType`) VALUES ("比亚迪仰望U8也用了增程？", "<p>仰望客服明确答复U8的发动机只能发电驱动车辆，无法机械直驱车辆，也就是说实际上就是增程式。而几个大的车网也明确标明U8是增程式</p><p></p>", "OFFLINE", "", 1682488118000, 1682488118000, 0, 0, "5ba1a83e33cf47d182b98ac1fe1beac8", NULL, "447bffd6152246c2092283a0a4d49bd8", "e8d1470f8c33b86d8dae444090e81be4", 0, 1);INSERT INTO `nodejs-service`.`sm_board_post_list` (`title`, `content`, `status`, `poster`, `createTime`, `updateTime`, `view`, `comment`, `authorId`, `commentId`, `categoryId`, `channelId`, `codeCount`, `postType`) VALUES ("小鹏的营销真的无语", "<p>看完动画，感觉小鹏汽车从名字到气质透着一股low，天天碰特斯拉，像牛皮膏药一样，没有一点骨气</p><p>-&nbsp;来自&nbsp;水木社区APP&nbsp;v3.5.7</p><p></p>", "OFFLINE", "", 1682429565377, 1682429565377, 0, 0, "5ba1a83e33cf47d182b98ac1fe1beac8", NULL, "447bffd6152246c2092283a0a4d49bd8", "e8d1470f8c33b86d8dae444090e81be4", 0, 1);INSERT INTO `nodejs-service`.`sm_board_post_list` (`title`, `content`, `status`, `poster`, `createTime`, `updateTime`, `view`, `comment`, `authorId`, `commentId`, `categoryId`, `channelId`, `codeCount`, `postType`) VALUES ("新能源车新势力的核心竞争力是什么？", "<p>一直没搞明白</p><p>以前传统车商核心竞争力，不外是发动机技术，底盘调教，节油技术等等，这些都是需要积累的，国内几十年还没完全学会。</p><p>新势力才成立几年，他们的核心竞争力是什么呢？</p><p>是电动机技术，独创的充电技术，储能电池技术？</p><p></p>", "OFFLINE", "", 1682493403000, 1682493403000, 0, 0, "5ba1a83e33cf47d182b98ac1fe1beac8", NULL, "447bffd6152246c2092283a0a4d49bd8", "e8d1470f8c33b86d8dae444090e81be4", 0, 1);INSERT INTO `nodejs-service`.`sm_board_post_list` (`title`, `content`, `status`, `poster`, `createTime`, `updateTime`, `view`, `comment`, `authorId`, `commentId`, `categoryId`, `channelId`, `codeCount`, `postType`) VALUES ("自己验车好通过么？", "<p>最近保险公司报价都没有代验车服务了，想问下10年以上的老车验车费劲么，需不需要加钱要待验车服务啊。</p><p></p>", "OFFLINE", "", 1682239610000, 1682239610000, 0, 0, "5ba1a83e33cf47d182b98ac1fe1beac8", NULL, "1c455a5dccf4242008d188f9676e3f4e", "e8d1470f8c33b86d8dae444090e81be4", 0, 1);INSERT INTO `nodejs-service`.`sm_board_post_list` (`title`, `content`, `status`, `poster`, `createTime`, `updateTime`, `view`, `comment`, `authorId`, `commentId`, `categoryId`, `channelId`, `codeCount`, `postType`) VALUES ("高速开经济模式还是运动模式？", "<p>有人说运动模式好，有人说经济模式好，到底开哪种模式好呢？</p><p></p>", "OFFLINE", "", 1682398871000, 1682398871000, 0, 0, "5ba1a83e33cf47d182b98ac1fe1beac8", NULL, "1c455a5dccf4242008d188f9676e3f4e", "e8d1470f8c33b86d8dae444090e81be4", 0, 1);INSERT INTO `nodejs-service`.`sm_board_post_list` (`title`, `content`, `status`, `poster`, `createTime`, `updateTime`, `view`, `comment`, `authorId`, `commentId`, `categoryId`, `channelId`, `codeCount`, `postType`) VALUES ("这个论坛里很多帖子，永远在打击贬低比亚迪", "<p>至少比亚迪dmi，开创了历史，不是世界也是国内</p><p>比亚迪仰望可以涉水行车，可以原地调头，可以1000多马力，而且是量产车，没有几百万。</p><p>比亚迪u9百公里不到四秒，三轮可以行驶，演示了，那一款车有这个技术，演示一下吧。</p><p></p>", "OFFLINE", "", 1682425950000, 1682425950000, 0, 0, "5ba1a83e33cf47d182b98ac1fe1beac8", NULL, "447bffd6152246c2092283a0a4d49bd8", "e8d1470f8c33b86d8dae444090e81be4", 0, 1);INSERT INTO `nodejs-service`.`sm_board_post_list` (`title`, `content`, `status`, `poster`, `createTime`, `updateTime`, `view`, `comment`, `authorId`, `commentId`, `categoryId`, `channelId`, `codeCount`, `postType`) VALUES ("明年2季度小鹏销量重回新势力第一，否则我退出水木", "<p>为什么这么说呢？理由如下。</p><p>新旗舰G9订单和产能逐渐释放，底盘和操控的口碑逐渐被消费者所认知，800伏高压超充的技术标杆更是业界标杆。</p><p>P7上市三年以后，终于迎来中期改款。换装了激光雷达和全新的智能座舱，800v的本领也继承下来。重新成为新势力轿车智能化和充电效率的领头羊。</p><p>对标model外的G7也全新上市，与P7一起成为小鹏销量的压舱石。</p><p>以上三款车保守销量在月销1.5到2万之间。</p><p>师母已呆。</p><p></p>", "OFFLINE", "", 1664878713000, 1664878713000, 0, 0, "5ba1a83e33cf47d182b98ac1fe1beac8", NULL, "447bffd6152246c2092283a0a4d49bd8", "e8d1470f8c33b86d8dae444090e81be4", 0, 1);INSERT INTO `nodejs-service`.`sm_board_post_list` (`title`, `content`, `status`, `poster`, `createTime`, `updateTime`, `view`, `comment`, `authorId`, `commentId`, `categoryId`, `channelId`, `codeCount`, `postType`) VALUES ("汽车的前避光垫有没有用，要不要装呢？", "<p></p>", "OFFLINE", "", 1682477635000, 1682477635000, 0, 0, "5ba1a83e33cf47d182b98ac1fe1beac8", NULL, "1c455a5dccf4242008d188f9676e3f4e", "e8d1470f8c33b86d8dae444090e81be4", 0, 1);INSERT INTO `nodejs-service`.`sm_board_post_list` (`title`, `content`, `status`, `poster`, `createTime`, `updateTime`, `view`, `comment`, `authorId`, `commentId`, `categoryId`, `channelId`, `codeCount`, `postType`) VALUES ("刚看到小鹏拉钉子的动画", "<p>创意挺好啊，</p><p>据视频评论说现在一边倒抨击这个动画吗，是这样吗？</p><p>竞争对手相互调侃不是也很有意思嘛</p><p></p>", "OFFLINE", "", 1682418111545, 1682418111545, 0, 0, "5ba1a83e33cf47d182b98ac1fe1beac8", NULL, "447bffd6152246c2092283a0a4d49bd8", "e8d1470f8c33b86d8dae444090e81be4", 0, 1);INSERT INTO `nodejs-service`.`sm_board_post_list` (`title`, `content`, `status`, `poster`, `createTime`, `updateTime`, `view`, `comment`, `authorId`, `commentId`, `categoryId`, `channelId`, `codeCount`, `postType`) VALUES ("e300L副驾座椅", "<p>1月底提车，一直是lp上下班开，副驾一直没坐人。</p><p>前天我正好坐了一次，发现座椅通风开了没变化，腿托调节无效。拍了视频给销售，约了周末去4s店检测。</p><p>北京奔驰可能真的有问题，个人感觉这种高度集成化的部件故障，应该过不了出场检测。</p><p></p>", "OFFLINE", "", 1682489104000, 1682489104000, 0, 0, "5ba1a83e33cf47d182b98ac1fe1beac8", NULL, "1c455a5dccf4242008d188f9676e3f4e", "e8d1470f8c33b86d8dae444090e81be4", 0, 1);INSERT INTO `nodejs-service`.`sm_board_post_list` (`title`, `content`, `status`, `poster`, `createTime`, `updateTime`, `view`, `comment`, `authorId`, `commentId`, `categoryId`, `channelId`, `codeCount`, `postType`) VALUES ("车停车位上，车标被人给掰走了", "<p>一个破福克斯，前标居然被掰走了，能用也行啊，关键掰了之后就不能用，也不知道哪个没屁眼的玩意儿干的，附近还没摄像头。</p><p></p>", "OFFLINE", "", 1681872152000, 1681872152000, 0, 0, "5ba1a83e33cf47d182b98ac1fe1beac8", NULL, "1c455a5dccf4242008d188f9676e3f4e", "e8d1470f8c33b86d8dae444090e81be4", 0, 1);INSERT INTO `nodejs-service`.`sm_board_post_list` (`title`, `content`, `status`, `poster`, `createTime`, `updateTime`, `view`, `comment`, `authorId`, `commentId`, `categoryId`, `channelId`, `codeCount`, `postType`) VALUES ("媳妇每次顺风车最怕打到电车", "<p>媳妇每天顺风车下班，北四环到顺义</p><p>最怕打到顺风车是电车，刚才跟我说零跑C01，直接半路吐了。</p><p>这是为啥，底盘不够好？还是加速太快导致的？</p><p></p>", "OFFLINE", "", 1682335400000, 1682335400000, 0, 0, "5ba1a83e33cf47d182b98ac1fe1beac8", NULL, "447bffd6152246c2092283a0a4d49bd8", "e8d1470f8c33b86d8dae444090e81be4", 0, 1);INSERT INTO `nodejs-service`.`sm_board_post_list` (`title`, `content`, `status`, `poster`, `createTime`, `updateTime`, `view`, `comment`, `authorId`, `commentId`, `categoryId`, `channelId`, `codeCount`, `postType`) VALUES ("雷克萨斯IS咋样", "<p>雷克萨斯IS咋样，适合男的开吗</p><p></p>", "OFFLINE", "", 1682482996000, 1682482996000, 0, 0, "5ba1a83e33cf47d182b98ac1fe1beac8", NULL, "1c455a5dccf4242008d188f9676e3f4e", "e8d1470f8c33b86d8dae444090e81be4", 0, 1);INSERT INTO `nodejs-service`.`sm_board_post_list` (`title`, `content`, `status`, `poster`, `createTime`, `updateTime`, `view`, `comment`, `authorId`, `commentId`, `categoryId`, `channelId`, `codeCount`, `postType`) VALUES ("验车价格", "<p>刚刚路过G6辅路，发现路边有一个检车场的牌子。</p><p>北京金峰盛机动车检测中心</p><p>好像以前没看见过。难道是新开的？看看是不是会便宜一点？正好这个月要验车了。</p><p>打电话问了一下多少钱？400。</p><p>然后又打了一个电话到经常去的学院路检车场，390。</p><p>算了，还是学院路吧。</p><p>发自「今日水木&nbsp;on&nbsp;iOS」</p><p></p>", "OFFLINE", "", 1681978800000, 1681978800000, 0, 0, "5ba1a83e33cf47d182b98ac1fe1beac8", NULL, "1c455a5dccf4242008d188f9676e3f4e", "e8d1470f8c33b86d8dae444090e81be4", 0, 1);INSERT INTO `nodejs-service`.`sm_board_post_list` (`title`, `content`, `status`, `poster`, `createTime`, `updateTime`, `view`, `comment`, `authorId`, `commentId`, `categoryId`, `channelId`, `codeCount`, `postType`) VALUES ("DMI为啥叫新能源", "<p>本质不是油车吗？</p><p></p>", "OFFLINE", "", 1682478688000, 1682478688000, 0, 0, "5ba1a83e33cf47d182b98ac1fe1beac8", NULL, "447bffd6152246c2092283a0a4d49bd8", "e8d1470f8c33b86d8dae444090e81be4", 0, 1);INSERT INTO `nodejs-service`.`sm_board_post_list` (`title`, `content`, `status`, `poster`, `createTime`, `updateTime`, `view`, `comment`, `authorId`, `commentId`, `categoryId`, `channelId`, `codeCount`, `postType`) VALUES ("花虎沟验车390", "<p>进门到出门30分钟</p><p>发自「今日水木&nbsp;on&nbsp;普京办公室」</p><p></p>", "OFFLINE", "", 1682469290000, 1682469290000, 0, 0, "5ba1a83e33cf47d182b98ac1fe1beac8", NULL, "1c455a5dccf4242008d188f9676e3f4e", "e8d1470f8c33b86d8dae444090e81be4", 0, 1);INSERT INTO `nodejs-service`.`sm_board_post_list` (`title`, `content`, `status`, `poster`, `createTime`, `updateTime`, `view`, `comment`, `authorId`, `commentId`, `categoryId`, `channelId`, `codeCount`, `postType`) VALUES ("预算80w，买什么车好泡妞", "<p>不要电车，不考虑两座跑车，什么车好泡妞？</p><p></p>", "OFFLINE", "", 1682435850000, 1682435850000, 0, 0, "5ba1a83e33cf47d182b98ac1fe1beac8", NULL, "1c455a5dccf4242008d188f9676e3f4e", "e8d1470f8c33b86d8dae444090e81be4", 0, 1);INSERT INTO `nodejs-service`.`sm_board_post_list` (`title`, `content`, `status`, `poster`, `createTime`, `updateTime`, `view`, `comment`, `authorId`, `commentId`, `categoryId`, `channelId`, `codeCount`, `postType`) VALUES ("比亚迪海鸥正式上市，价格比预售价低5000", "<p>比亚迪海鸥公布正式售价，三款车7.38万、7.88万、8.98万。</p><p></p><p><img src='http://192.168.200.130:9000/blog-service-oss/20230426/fbc3fc6ff6364ec3b77e3fe59a199f64.jpg' alt='' data-src='http://192.168.200.130:9000/blog-service-oss/20230426/fbc3fc6ff6364ec3b77e3fe59a199f64.jpg' /></p><p><img src='http://192.168.200.130:9000/blog-service-oss/20230426/64a4b343a58446c3b2208ad4b2e9e3ba.jpg' alt='' data-src='http://192.168.200.130:9000/blog-service-oss/20230426/64a4b343a58446c3b2208ad4b2e9e3ba.jpg' /></p>", "OFFLINE", "", 1682494219000, 1682494219000, 0, 0, "5ba1a83e33cf47d182b98ac1fe1beac8", NULL, "447bffd6152246c2092283a0a4d49bd8", "e8d1470f8c33b86d8dae444090e81be4", 0, 1);INSERT INTO `nodejs-service`.`sm_board_post_list` (`title`, `content`, `status`, `poster`, `createTime`, `updateTime`, `view`, `comment`, `authorId`, `commentId`, `categoryId`, `channelId`, `codeCount`, `postType`) VALUES ("跑山利器是啥车？", "<p>有实际开过的说说，山路上加减速、高速过弯等等操控好、不费劲的车。</p><p></p>", "OFFLINE", "", 1682470213000, 1682470213000, 0, 0, "5ba1a83e33cf47d182b98ac1fe1beac8", NULL, "1c455a5dccf4242008d188f9676e3f4e", "e8d1470f8c33b86d8dae444090e81be4", 0, 1);INSERT INTO `nodejs-service`.`sm_board_post_list` (`title`, `content`, `status`, `poster`, `createTime`, `updateTime`, `view`, `comment`, `authorId`, `commentId`, `categoryId`, `channelId`, `codeCount`, `postType`) VALUES ("新势力还没开始盈利就被卷死了", "<p>太惨了！</p><p></p>", "OFFLINE", "", 1682436099000, 1682436099000, 0, 0, "5ba1a83e33cf47d182b98ac1fe1beac8", NULL, "1c455a5dccf4242008d188f9676e3f4e", "e8d1470f8c33b86d8dae444090e81be4", 0, 1);INSERT INTO `nodejs-service`.`sm_board_post_list` (`title`, `content`, `status`, `poster`, `createTime`, `updateTime`, `view`, `comment`, `authorId`, `commentId`, `categoryId`, `channelId`, `codeCount`, `postType`) VALUES ("上周末跑了一个长途", "<p>凌晨五点天不亮出发，下午四点回来，全程来回共1090km。&nbsp;就是到目的地办事停了一下，办完事加了个油就往回赶，来回途中不停不歇，没吃一口没喝一口。</p><p>来回的表显均速110kph，全程平均油耗8.1。</p><p>这种用车情况电车肯定没戏了吧，不过这种场景不常出现。</p><p></p>", "OFFLINE", "", 1681915461000, 1681915461000, 0, 0, "5ba1a83e33cf47d182b98ac1fe1beac8", NULL, "1c455a5dccf4242008d188f9676e3f4e", "e8d1470f8c33b86d8dae444090e81be4", 0, 1);INSERT INTO `nodejs-service`.`sm_board_post_list` (`title`, `content`, `status`, `poster`, `createTime`, `updateTime`, `view`, `comment`, `authorId`, `commentId`, `categoryId`, `channelId`, `codeCount`, `postType`) VALUES ("临牌可以出京吗？", "<p>看了一些网站说要扣12分罚200元</p><p>但是问了一个专门办理上牌的人，说可以出京，专业人士不会出错吧？</p><p>所以有点懵B了</p><p></p>", "OFFLINE", "", 1682470205000, 1682470205000, 0, 0, "5ba1a83e33cf47d182b98ac1fe1beac8", NULL, "1c455a5dccf4242008d188f9676e3f4e", "e8d1470f8c33b86d8dae444090e81be4", 0, 1);INSERT INTO `nodejs-service`.`sm_board_post_list` (`title`, `content`, `status`, `poster`, `createTime`, `updateTime`, `view`, `comment`, `authorId`, `commentId`, `categoryId`, `channelId`, `codeCount`, `postType`) VALUES ("辅助驾驶到底有没有用？", "<p>我开高速连定速巡航都是鸡肋，生怕走神把自己带到沟里去。开车还是要百分百专注，辅助驾驶是否会影响开车的专注度，让你掉以轻心，进而造成事故？</p><p></p>", "OFFLINE", "", 1682482237000, 1682482237000, 0, 0, "5ba1a83e33cf47d182b98ac1fe1beac8", NULL, "1c455a5dccf4242008d188f9676e3f4e", "e8d1470f8c33b86d8dae444090e81be4", 0, 1);INSERT INTO `nodejs-service`.`sm_board_post_list` (`title`, `content`, `status`, `poster`, `createTime`, `updateTime`, `view`, `comment`, `authorId`, `commentId`, `categoryId`, `channelId`, `codeCount`, `postType`) VALUES ("斯巴鲁傲虎怎么样？", "<p>rt</p><p></p>", "OFFLINE", "", 1682391455000, 1682391455000, 0, 0, "5ba1a83e33cf47d182b98ac1fe1beac8", NULL, "1c455a5dccf4242008d188f9676e3f4e", "e8d1470f8c33b86d8dae444090e81be4", 0, 1);INSERT INTO `nodejs-service`.`sm_board_post_list` (`title`, `content`, `status`, `poster`, `createTime`, `updateTime`, `view`, `comment`, `authorId`, `commentId`, `categoryId`, `channelId`, `codeCount`, `postType`) VALUES ("蔚来小鹏等新势力的试驾有羊毛吗？", "<p>送油卡京东卡什么的</p><p>感觉再不薅就该倒闭了</p><p>发自「今日水木&nbsp;on&nbsp;RMX3350」</p><p></p>", "OFFLINE", "", 1682424244000, 1682424244000, 0, 0, "5ba1a83e33cf47d182b98ac1fe1beac8", NULL, "1c455a5dccf4242008d188f9676e3f4e", "e8d1470f8c33b86d8dae444090e81be4", 0, 1);INSERT INTO `nodejs-service`.`sm_board_post_list` (`title`, `content`, `status`, `poster`, `createTime`, `updateTime`, `view`, `comment`, `authorId`, `commentId`, `categoryId`, `channelId`, `codeCount`, `postType`) VALUES ("氢能源车在消费者端没有竞争力", "<p>如果拿电车，氢车，油车比较</p><p>燃料成本电车最低，然后是氢车，油车</p><p>造车成本，最低的是油车，然后是电车，氢车不光要有电车的一套东西，还要加上氢电转换的成本</p><p>所以驾驶体验上，电车最好，然后是油车，最后是氢车</p><p>所以对于油车车主，用高价格，换相对不太好的驾驶体验，怎么想也觉得推不动</p><p>跟电车比，就更是被甩了8条街了</p><p>-&nbsp;来自&nbsp;水木社区APP&nbsp;v3.5.7</p><p></p>", "OFFLINE", "", 1681779159000, 1681779159000, 0, 0, "5ba1a83e33cf47d182b98ac1fe1beac8", NULL, "447bffd6152246c2092283a0a4d49bd8", "e8d1470f8c33b86d8dae444090e81be4", 0, 1);INSERT INTO `nodejs-service`.`sm_board_post_list` (`title`, `content`, `status`, `poster`, `createTime`, `updateTime`, `view`, `comment`, `authorId`, `commentId`, `categoryId`, `channelId`, `codeCount`, `postType`) VALUES ("自动驾驶我有个疑问", "<p>我有点疑惑，只要自动驾驶的事故率降低到和现在人开一样，就可以实际应用起来了呀，&nbsp;保险公司一样该赔偿赔偿，该提价提价就行了呗。</p><p>为啥会不敢搞了呢</p><p></p>", "OFFLINE", "", 1682405334000, 1682405334000, 0, 0, "5ba1a83e33cf47d182b98ac1fe1beac8", NULL, "447bffd6152246c2092283a0a4d49bd8", "e8d1470f8c33b86d8dae444090e81be4", 0, 1);INSERT INTO `nodejs-service`.`sm_board_post_list` (`title`, `content`, `status`, `poster`, `createTime`, `updateTime`, `view`, `comment`, `authorId`, `commentId`, `categoryId`, `channelId`, `codeCount`, `postType`) VALUES ("唐很难停车吗？", "<p>有车主分享么</p><p></p>", "OFFLINE", "", 1682384209000, 1682384209000, 0, 0, "5ba1a83e33cf47d182b98ac1fe1beac8", NULL, "447bffd6152246c2092283a0a4d49bd8", "e8d1470f8c33b86d8dae444090e81be4", 0, 1);INSERT INTO `nodejs-service`.`sm_board_post_list` (`title`, `content`, `status`, `poster`, `createTime`, `updateTime`, `view`, `comment`, `authorId`, `commentId`, `categoryId`, `channelId`, `codeCount`, `postType`) VALUES ("2017款速腾1.4T自动豪华", "<p>12W公里</p><p>能卖多少钱</p><p></p>", "OFFLINE", "", 1682487462000, 1682487462000, 0, 0, "5ba1a83e33cf47d182b98ac1fe1beac8", NULL, "1c455a5dccf4242008d188f9676e3f4e", "e8d1470f8c33b86d8dae444090e81be4", 0, 1);INSERT INTO `nodejs-service`.`sm_board_post_list` (`title`, `content`, `status`, `poster`, `createTime`, `updateTime`, `view`, `comment`, `authorId`, `commentId`, `categoryId`, `channelId`, `codeCount`, `postType`) VALUES ("看看比亚迪的招聘，这不比合资企业岗位招聘薪酬高多了？", "<p>如图</p><p></p><p><img src='http://192.168.200.130:9000/blog-service-oss/20230426/70f227469c584843bfbe8ef1ed311d11.jpg' alt='' data-src='http://192.168.200.130:9000/blog-service-oss/20230426/70f227469c584843bfbe8ef1ed311d11.jpg' /></p>", "OFFLINE", "", 1682490085000, 1682490085000, 0, 0, "5ba1a83e33cf47d182b98ac1fe1beac8", NULL, "447bffd6152246c2092283a0a4d49bd8", "e8d1470f8c33b86d8dae444090e81be4", 0, 1);INSERT INTO `nodejs-service`.`sm_board_post_list` (`title`, `content`, `status`, `poster`, `createTime`, `updateTime`, `view`, `comment`, `authorId`, `commentId`, `categoryId`, `channelId`, `codeCount`, `postType`) VALUES ("别看中国人看不上海鸥，其实这车最可能给燃油车致命一击", "<p></p>", "OFFLINE", "", 1682493427000, 1682493427000, 0, 0, "5ba1a83e33cf47d182b98ac1fe1beac8", NULL, "447bffd6152246c2092283a0a4d49bd8", "e8d1470f8c33b86d8dae444090e81be4", 0, 1);INSERT INTO `nodejs-service`.`sm_board_post_list` (`title`, `content`, `status`, `poster`, `createTime`, `updateTime`, `view`, `comment`, `authorId`, `commentId`, `categoryId`, `channelId`, `codeCount`, `postType`) VALUES ("如果特斯拉全系降2万元，哪些车企受影响最大？", "<p>有没有因此倒闭的？</p><p></p>", "OFFLINE", "", 1682489041000, 1682489041000, 0, 0, "5ba1a83e33cf47d182b98ac1fe1beac8", NULL, "447bffd6152246c2092283a0a4d49bd8", "e8d1470f8c33b86d8dae444090e81be4", 0, 1);INSERT INTO `nodejs-service`.`sm_board_post_list` (`title`, `content`, `status`, `poster`, `createTime`, `updateTime`, `view`, `comment`, `authorId`, `commentId`, `categoryId`, `channelId`, `codeCount`, `postType`) VALUES ("蔚来就是电车中的沃尔沃吧", "<p>我发现高知都喜欢蔚来，和沃尔沃是一个人群</p><p></p>", "OFFLINE", "", 1682415509000, 1682415509000, 0, 0, "5ba1a83e33cf47d182b98ac1fe1beac8", NULL, "447bffd6152246c2092283a0a4d49bd8", "e8d1470f8c33b86d8dae444090e81be4", 0, 1);INSERT INTO `nodejs-service`.`sm_board_post_list` (`title`, `content`, `status`, `poster`, `createTime`, `updateTime`, `view`, `comment`, `authorId`, `commentId`, `categoryId`, `channelId`, `codeCount`, `postType`) VALUES ("晕电车的报个身高体重？", "<p>我先来</p><p>175cm,85kg，体脂秤说内脏脂肪过多</p><p>在想，是不是胖子更晕。。。。</p><p></p>", "OFFLINE", "", 1682426641000, 1682426641000, 0, 0, "5ba1a83e33cf47d182b98ac1fe1beac8", NULL, "447bffd6152246c2092283a0a4d49bd8", "e8d1470f8c33b86d8dae444090e81be4", 0, 1);INSERT INTO `nodejs-service`.`sm_board_post_list` (`title`, `content`, `status`, `poster`, `createTime`, `updateTime`, `view`, `comment`, `authorId`, `commentId`, `categoryId`, `channelId`, `codeCount`, `postType`) VALUES ("蔚来：五一期间免费不限次换电", "<p>蔚来宣布4月28日至5月4日，全国362座高速公路换电站向所有蔚来车辆提供不限次数的免费换电服务（营运车辆除外），其中高速服务区换电站将24小时提供服务</p><p>目前，蔚来在中国市场已累计建成换电站1,369座（其中高速公路换电站362座）、充电站2,512座（充电桩14,903根），接入第三方充电桩超69万根</p><p>https://www.autoreport.cn/zonghexinwen/20230425/1080942082.html</p><p></p>", "OFFLINE", "", 1682492446000, 1682492446000, 0, 0, "5ba1a83e33cf47d182b98ac1fe1beac8", NULL, "447bffd6152246c2092283a0a4d49bd8", "e8d1470f8c33b86d8dae444090e81be4", 0, 1);INSERT INTO `nodejs-service`.`sm_board_post_list` (`title`, `content`, `status`, `poster`, `createTime`, `updateTime`, `view`, `comment`, `authorId`, `commentId`, `categoryId`, `channelId`, `codeCount`, `postType`) VALUES ("[原创][讨论]出租闲置小客车指标", "<p>我爱人闲置指标&nbsp;出租&nbsp;要求对方在北京有稳定工作&nbsp;外地人北京人无所谓&nbsp;人靠谱踏实就行&nbsp;&nbsp;必须工作稳定&nbsp;征信良好&nbsp;芝麻信用700左右&nbsp;我们两口子事少&nbsp;希望对方驾龄五年以上&nbsp;配合抵押公正&nbsp;三者保险必须二百万以上。&nbsp;中介勿扰。&nbsp;V13146166625</p><p></p>", "OFFLINE", "", 1681811171000, 1681811171000, 0, 0, "5ba1a83e33cf47d182b98ac1fe1beac8", NULL, "cbd3db9f308a19847666f22ab2a29075", "e8d1470f8c33b86d8dae444090e81be4", 0, 1);INSERT INTO `nodejs-service`.`sm_board_post_list` (`title`, `content`, `status`, `poster`, `createTime`, `updateTime`, `view`, `comment`, `authorId`, `commentId`, `categoryId`, `channelId`, `codeCount`, `postType`) VALUES ("yyp评测说极氪001的悬挂舒适性比奔驰s要好", "<p>没想到老牌帝国这么拉了</p><p></p>", "OFFLINE", "", 1682474051000, 1682474051000, 0, 0, "5ba1a83e33cf47d182b98ac1fe1beac8", NULL, "447bffd6152246c2092283a0a4d49bd8", "e8d1470f8c33b86d8dae444090e81be4", 0, 1);INSERT INTO `nodejs-service`.`sm_board_post_list` (`title`, `content`, `status`, `poster`, `createTime`, `updateTime`, `view`, `comment`, `authorId`, `commentId`, `categoryId`, `channelId`, `codeCount`, `postType`) VALUES ("我是真不信比亚迪能有什么技术", "<p>一个科技公司，现在给研究生开的工资才7k，当然了，给c9的能达到13k，这打法乞丐呢？</p><p>有几个愿意去的？能招到牛人？我是不信的</p><p>所以比亚迪的所有技术，都是做到60分就开始吹牛逼，我真的一丁点都看不惯</p><p></p>", "OFFLINE", "", 1682070180000, 1682070180000, 0, 0, "5ba1a83e33cf47d182b98ac1fe1beac8", NULL, "447bffd6152246c2092283a0a4d49bd8", "e8d1470f8c33b86d8dae444090e81be4", 0, 1);INSERT INTO `nodejs-service`.`sm_board_post_list` (`title`, `content`, `status`, `poster`, `createTime`, `updateTime`, `view`, `comment`, `authorId`, `commentId`, `categoryId`, `channelId`, `codeCount`, `postType`) VALUES ("想买个油车，求推荐", "<p>预算20-30w</p><p>需求：按优先级排</p><p>1）安全</p><p>2）车内气味小，环保</p><p>3）开高速不累</p><p></p>", "OFFLINE", "", 1682476568000, 1682476568000, 0, 0, "5ba1a83e33cf47d182b98ac1fe1beac8", NULL, "1c455a5dccf4242008d188f9676e3f4e", "e8d1470f8c33b86d8dae444090e81be4", 0, 1);INSERT INTO `nodejs-service`.`sm_board_post_list` (`title`, `content`, `status`, `poster`, `createTime`, `updateTime`, `view`, `comment`, `authorId`, `commentId`, `categoryId`, `channelId`, `codeCount`, `postType`) VALUES ("余大嘴真要失业了？", "<p>都在传他被调岗了，不再负责车BU了，不知道真的假的，如果是被调岗，那离失业应该不远了</p><p></p>", "OFFLINE", "", 1682344348000, 1682344348000, 0, 0, "5ba1a83e33cf47d182b98ac1fe1beac8", NULL, "447bffd6152246c2092283a0a4d49bd8", "e8d1470f8c33b86d8dae444090e81be4", 0, 1);INSERT INTO `nodejs-service`.`sm_board_post_list` (`title`, `content`, `status`, `poster`, `createTime`, `updateTime`, `view`, `comment`, `authorId`, `commentId`, `categoryId`, `channelId`, `codeCount`, `postType`) VALUES ("周末看车，比亚迪的体验感最差", "<p>计划买车，初步锁定：MPV岚图梦想家；轿车飞凡F7；SUV唐或者ID6X（俩娃，这种车型十年内最合适）。</p><p>周末跑了四五个商场、汽车城看车，飞凡、大众、极狐、蔚来、特斯拉、问界、岚图、小鹏……全看了一遍，工作人员态度均不错。</p><p>把车型最多的比亚迪留到了最后，重点来了，</p><p>我老公这人理工直男，选车会问的比较细，把各参数记下来横向对比，他进门后跟销售说七八个车型都想了解一下。</p><p>期间销售一直问我们想要的价格区间，我们说哪个区间都可以，主要看车，唐是比较理想的。</p><p>最后询问销售各车型价格的时候，销售跟我们说问价格没意义，而且反复强调问价格没意义</p><p>问销售续航600实际跑多少，跟我们说标600就600，明显的不耐烦了……</p><p>我说我们买车也不是挑颗大白菜，多问几句不行？</p><p>反正比亚迪看车后的感觉就是：我销量高，不差你这辆，自己做好功课确定想下单再来，别唧唧歪歪多问，没工夫招呼。</p><p></p>", "OFFLINE", "", 1682384860000, 1682384860000, 0, 0, "5ba1a83e33cf47d182b98ac1fe1beac8", NULL, "447bffd6152246c2092283a0a4d49bd8", "e8d1470f8c33b86d8dae444090e81be4", 0, 1);INSERT INTO `nodejs-service`.`sm_board_post_list` (`title`, `content`, `status`, `poster`, `createTime`, `updateTime`, `view`, `comment`, `authorId`, `commentId`, `categoryId`, `channelId`, `codeCount`, `postType`) VALUES ("感觉比亚迪要弄死理想也就是分分钟的事", "<p>理想和蔚来的卖点都是车体大</p><p>如果比亚迪出个尺寸相当的dmi,&nbsp;就覆盖了理想所有的卖点，理想就岌岌可危了</p><p>反倒是蔚来可以活下来，“换电”提供了差异化竞争</p><p></p>", "OFFLINE", "", 1682475085000, 1682475085000, 0, 0, "5ba1a83e33cf47d182b98ac1fe1beac8", NULL, "447bffd6152246c2092283a0a4d49bd8", "e8d1470f8c33b86d8dae444090e81be4", 0, 1);INSERT INTO `nodejs-service`.`sm_board_post_list` (`title`, `content`, `status`, `poster`, `createTime`, `updateTime`, `view`, `comment`, `authorId`, `commentId`, `categoryId`, `channelId`, `codeCount`, `postType`) VALUES ("从就业角度讲，挺希望新势力全部活下来", "<p>新势力挖人的待遇、岗位、职场文化才有点先进企业的样子</p><p>没有新势力搅局，</p><p>老势力还是抠抠搜搜、土了吧唧</p><p>合资、外企待遇不上不下，也没动力在中国搞先进研发</p><p></p>", "OFFLINE", "", 1682491906000, 1682491906000, 0, 0, "5ba1a83e33cf47d182b98ac1fe1beac8", NULL, "447bffd6152246c2092283a0a4d49bd8", "e8d1470f8c33b86d8dae444090e81be4", 0, 1);INSERT INTO `nodejs-service`.`sm_board_post_list` (`title`, `content`, `status`, `poster`, `createTime`, `updateTime`, `view`, `comment`, `authorId`, `commentId`, `categoryId`, `channelId`, `codeCount`, `postType`) VALUES ("B厂看来是急了", "<p>海鸥上市价比预售价降了5K，看来预售订单情况并不理想</p><p></p>", "OFFLINE", "", 1682493986000, 1682493986000, 0, 0, "5ba1a83e33cf47d182b98ac1fe1beac8", NULL, "447bffd6152246c2092283a0a4d49bd8", "e8d1470f8c33b86d8dae444090e81be4", 0, 1);INSERT INTO `nodejs-service`.`sm_board_post_list` (`title`, `content`, `status`, `poster`, `createTime`, `updateTime`, `view`, `comment`, `authorId`, `commentId`, `categoryId`, `channelId`, `codeCount`, `postType`) VALUES ("50w纯电suv求推荐", "<p>预算50左右，四大一小，大五座或者七座2-3-2，要求空间大，舒适，豪华感，操控好，续航高，智能驾驶水平高。</p><p>对新能源没什么关注和研究，喜欢蔚来es6/7，但怕倒闭，而且续航差。纯为家用model&nbsp;y勿推荐，空间舒适不行。油车混动勿推荐，没指标。</p><p>或者未来两年内明确会上市的车型，也可以。</p><p>发自「今日水木&nbsp;on&nbsp;iPhone&nbsp;13」</p><p></p>", "OFFLINE", "", 1682379071000, 1682379071000, 0, 0, "5ba1a83e33cf47d182b98ac1fe1beac8", NULL, "447bffd6152246c2092283a0a4d49bd8", "e8d1470f8c33b86d8dae444090e81be4", 0, 1);INSERT INTO `nodejs-service`.`sm_board_post_list` (`title`, `content`, `status`, `poster`, `createTime`, `updateTime`, `view`, `comment`, `authorId`, `commentId`, `categoryId`, `channelId`, `codeCount`, `postType`) VALUES ("右转直行道，被直行的占着等红灯可以谴责吗", "<p>规则上肯定是没问题，但碰到好多次直行道上车也不多，非要占着右转的道，后面一排右转的都等他</p><p>反正我自己直行的时候，能排直行道就排直行道，除非红灯马上变绿灯了，就会去直行右转道</p><p></p>", "OFFLINE", "", 1682492568000, 1682492568000, 0, 0, "5ba1a83e33cf47d182b98ac1fe1beac8", NULL, "1c455a5dccf4242008d188f9676e3f4e", "e8d1470f8c33b86d8dae444090e81be4", 0, 1);INSERT INTO `nodejs-service`.`sm_board_post_list` (`title`, `content`, `status`, `poster`, `createTime`, `updateTime`, `view`, `comment`, `authorId`, `commentId`, `categoryId`, `channelId`, `codeCount`, `postType`) VALUES ("双门小钢炮，是电动车的未来主流发展方向。", "<p>双门小钢炮，是电动车的未来主流发展方向。</p><p></p>", "OFFLINE", "", 1676868192000, 1676868192000, 0, 0, "5ba1a83e33cf47d182b98ac1fe1beac8", NULL, "447bffd6152246c2092283a0a4d49bd8", "e8d1470f8c33b86d8dae444090e81be4", 0, 1);INSERT INTO `nodejs-service`.`sm_board_post_list` (`title`, `content`, `status`, `poster`, `createTime`, `updateTime`, `view`, `comment`, `authorId`, `commentId`, `categoryId`, `channelId`, `codeCount`, `postType`) VALUES ("捷豹F-pace", "<p>上周去带娃上课，期间旁边有捷豹路虎4s店，问了下F-pace，小哥热情约试驾，开了一圈，还不错，除了有点撑船的感觉。目前是9.5代雅阁，可能习惯了轿车。。</p><p>后面问价格，RD款（标价50万那款），落地39，还没谈。。现车灰外红内。其他要等半年漂洋过海了。</p><p>想想不比330差呀，海外号称对标macan。</p><p>有点心动。。。</p><p>不知道有车友用过这车吗？</p><p>发自「今日水木&nbsp;on&nbsp;LIO-AN00」</p><p></p>", "OFFLINE", "", 1682495027000, 1682495027000, 0, 0, "5ba1a83e33cf47d182b98ac1fe1beac8", NULL, "1c455a5dccf4242008d188f9676e3f4e", "e8d1470f8c33b86d8dae444090e81be4", 0, 1);INSERT INTO `nodejs-service`.`sm_board_post_list` (`title`, `content`, `status`, `poster`, `createTime`, `updateTime`, `view`, `comment`, `authorId`, `commentId`, `categoryId`, `channelId`, `codeCount`, `postType`) VALUES ("飞凡F7一周驾驶体验", "<p>优点：</p><p>1、外观漂亮回头率高；</p><p>2、座椅比较舒服，跟宣传的差不多；</p><p>3、后备箱比较大，我已经放了一堆东西了，大掀背也很帅；</p><p>4、重点说一下辅助驾驶吧，因为辅助驾驶确实很难在试驾的时候体验出来。我个人给辅助驾驶打80分，我没体验过其他车的辅助驾驶，没有对比，但是F7确实满足了我主要的需求。在环路或高速封闭路段，我都会开启，回个微信看下手机很放心，遇到强行加塞也能及时减速避让。（有一次在四环上迷着睡了十几分钟没啥事）</p><p>5、NVH非常棒，无边框车门能做到这个效果值得称赞；</p><p>6、电耗:北京最近气温15-20度左右，我开了200KM高速时速平均120，500KM的通勤，偶尔开空调，总体续航达成率86%；</p><p>7、天幕隔热效果很好，太阳晒一下午也不会觉得暴热；</p><p>8、360度影像倒车很好用；</p><p>9、方向盘设计的比较合理常见控制可以按键实现；</p><p>缺点：</p><p>1、还是辅助驾驶，我选装了2W的高阶驾驶硬件，虽然能识别锥桶，但是3次均未做出避让，不知道是不是我智能驾驶里勾选了打灯变道的问题（近期再试试，我下班有个固定点有锥桶道路变窄）？但是确实没减速没变道，所以自动驾驶建议走中间车道或者内车道。遇到道路变窄的情况也是可以识别但无法实现变道。</p><p>2、语音控制不够智能，必须要把它的菜单名称说出来，比如设置导航为简洁播报，系统识别不了，要说导航播报简洁，因为导航播报是菜单名。这个缺点对新手车主很不友好。</p><p>3、打开车机必须坐在主驾，如果我想直接去副驾休息一会儿，则无法开启车机听音乐&nbsp;空调等功能。</p><p>4、这个车确实很宽（不知道算不算缺点），北京路边停车，内测轮胎贴马路牙子，外侧仍然会压在线上</p><p>（操控就不谈了，我以前的车是江淮第一代电车，太差了，现在提升很大但没有可比性。）</p><p>建议：</p><p>买个头枕，淘宝lamous头枕迈巴赫米颜色的跟白内饰完全一个色。</p><p>总体来说这个车我打90分吧，缺点基本都可以后期OTA升级解决，就看官方的效率了。</p><p>提车的和准备下定的北京车友可以加群，沟通日常使用过程中的问题：</p><p></p><p><img src='http://192.168.200.130:9000/blog-service-oss/20230426/f47e5c8369634136807d9952112c83aa.jpg' alt='' data-src='http://192.168.200.130:9000/blog-service-oss/20230426/f47e5c8369634136807d9952112c83aa.jpg' /></p>", "OFFLINE", "", 1682406949000, 1682406949000, 0, 0, "5ba1a83e33cf47d182b98ac1fe1beac8", NULL, "447bffd6152246c2092283a0a4d49bd8", "e8d1470f8c33b86d8dae444090e81be4", 0, 1);INSERT INTO `nodejs-service`.`sm_board_post_list` (`title`, `content`, `status`, `poster`, `createTime`, `updateTime`, `view`, `comment`, `authorId`, `commentId`, `categoryId`, `channelId`, `codeCount`, `postType`) VALUES ("定了零跑纯电c11丐版", "<p>希望早日提车</p><p></p>", "OFFLINE", "", 1682439363000, 1682439363000, 0, 0, "5ba1a83e33cf47d182b98ac1fe1beac8", NULL, "447bffd6152246c2092283a0a4d49bd8", "e8d1470f8c33b86d8dae444090e81be4", 0, 1);INSERT INTO `nodejs-service`.`sm_board_post_list` (`title`, `content`, `status`, `poster`, `createTime`, `updateTime`, `view`, `comment`, `authorId`, `commentId`, `categoryId`, `channelId`, `codeCount`, `postType`) VALUES ("纯电车其实渗透率很低，我去停车场一看，基本还是油车", "<p>纯电车其实渗透率很低，我去停车场一看，基本还是油车</p><p></p>", "OFFLINE", "", 1682217476000, 1682217476000, 0, 0, "5ba1a83e33cf47d182b98ac1fe1beac8", NULL, "447bffd6152246c2092283a0a4d49bd8", "e8d1470f8c33b86d8dae444090e81be4", 0, 1);INSERT INTO `nodejs-service`.`sm_board_post_list` (`title`, `content`, `status`, `poster`, `createTime`, `updateTime`, `view`, `comment`, `authorId`, `commentId`, `categoryId`, `channelId`, `codeCount`, `postType`) VALUES ("[求助]北京小客车过户外迁", "<p>有一辆汽车目前租了个北京牌，想过户到本人名下外迁到上海，请问具体怎么办理过户外迁手续啊，谢谢?</p><p></p>", "OFFLINE", "", 1682473423000, 1682473423000, 0, 0, "5ba1a83e33cf47d182b98ac1fe1beac8", NULL, "1c455a5dccf4242008d188f9676e3f4e", "e8d1470f8c33b86d8dae444090e81be4", 0, 1);INSERT INTO `nodejs-service`.`sm_board_post_list` (`title`, `content`, `status`, `poster`, `createTime`, `updateTime`, `view`, `comment`, `authorId`, `commentId`, `categoryId`, `channelId`, `codeCount`, `postType`) VALUES ("4月第3周各品牌销量排行top20", "<p></p><p><img src='http://192.168.200.130:9000/blog-service-oss/20230426/e067335714b747dfa6f575bae73093e7.jpg' alt='' data-src='http://192.168.200.130:9000/blog-service-oss/20230426/e067335714b747dfa6f575bae73093e7.jpg' /></p>", "OFFLINE", "", 1682440224000, 1682440224000, 0, 0, "5ba1a83e33cf47d182b98ac1fe1beac8", NULL, "1c455a5dccf4242008d188f9676e3f4e", "e8d1470f8c33b86d8dae444090e81be4", 0, 1);INSERT INTO `nodejs-service`.`sm_board_post_list` (`titl